import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [categorias, setCategorias] = useState([]);
  const [produtos, setProdutos] = useState([]);
  const [carregando, setCarregando] = useState(true);

  console.log('🔵 Componente App MONTADO');

  useEffect(() => {
    console.log('🟡 useEffect EXECUTANDO');
    
    const carregarDados = async () => {
      try {
        console.log('🟢 Iniciando fetch da API...');
        
        // TESTE 1: Buscar categorias
        console.log('📡 Fazendo requisição para /api/categorias');
        const respostaCategorias = await fetch('/api/categorias');
        console.log('✅ Resposta categorias:', respostaCategorias);
        
        const dadosCategorias = await respostaCategorias.json();
        console.log('📦 Dados categorias:', dadosCategorias);

        // TESTE 2: Buscar produtos
        console.log('📡 Fazendo requisição para /api/produtos');
        const respostaProdutos = await fetch('/api/produtos');
        console.log('✅ Resposta produtos:', respostaProdutos);
        
        const dadosProdutos = await respostaProdutos.json();
        console.log('📦 Dados produtos:', dadosProdutos);

        setCategorias(dadosCategorias);
        setProdutos(dadosProdutos);
        setCarregando(false);
        
        console.log('🎉 DADOS CARREGADOS COM SUCESSO!');
        
      } catch (erro) {
        console.error('❌ ERRO DETALHADO:', erro);
        setCarregando(false);
      }
    };

    carregarDados();
  }, []);

  console.log('🔄 Componente RENDERIZANDO, carregando:', carregando);

  if (carregando) {
    return (
      <div style={{ 
        padding: '50px', 
        textAlign: 'center',
        fontSize: '24px',
        color: '#b71c1c'
      }}>
        <h1>🍔 Jetro's Lanches</h1>
        <p>🔄 CARREGANDO CARDÁPIO...</p>
        <p>Verifique o Console (F12) para ver o progresso</p>
      </div>
    );
  }

  return (
    <div style={{ padding: '20px' }}>
      <header style={{ 
        backgroundColor: '#b71c1c', 
        color: 'white', 
        padding: '20px', 
        textAlign: 'center',
        borderRadius: '10px',
        marginBottom: '30px'
      }}>
        <h1>🍔 Jetro's Lanches - CARDÁPIO API 🎉</h1>
        <p>Dados carregados da API Laravel!</p>
      </header>

      <div style={{ textAlign: 'center', marginBottom: '30px' }}>
        <h2>✅ FUNCIONANDO!</h2>
        <p>
          <strong>{categorias.length}</strong> categorias | 
          <strong> {produtos.length}</strong> produtos
        </p>
      </div>

      <div style={{ backgroundColor: '#e8f5e8', padding: '20px', borderRadius: '10px' }}>
        <h3>🎯 Primeira categoria como teste:</h3>
        {categorias.length > 0 && (
          <div>
            <h4>{categorias[0].nome}</h4>
            <p>{categorias[0].descricao}</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
