import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [categorias, setCategorias] = useState([]);
  const [produtos, setProdutos] = useState([]);
  const [carregando, setCarregando] = useState(true);

  useEffect(() => {
    const carregarDados = async () => {
      try {
        console.log('üîÑ CARREGANDO DADOS...');
        
        const [resCategorias, resProdutos] = await Promise.all([
          fetch('/api/categorias'),
          fetch('/api/produtos')
        ]);

        const dadosCategorias = await resCategorias.json();
        const dadosProdutos = await resProdutos.json();

        console.log('üìä CATEGORIAS:', dadosCategorias);
        console.log('üì¶ PRODUTOS:', dadosProdutos);

        setCategorias(dadosCategorias);
        setProdutos(dadosProdutos);
        setCarregando(false);
        
      } catch (erro) {
        console.error('‚ùå ERRO:', erro);
        setCarregando(false);
      }
    };

    carregarDados();
  }, []);

  // TESTE SIMPLES - APENAS TEXTO
  return (
    <div style={{ padding: '50px', textAlign: 'center', backgroundColor: 'lightblue' }}>
      <h1 style={{ color: 'red', fontSize: '40px' }}>üçî JETRO'S LANCHES</h1>
      
      <div style={{ backgroundColor: 'yellow', padding: '20px', margin: '20px' }}>
        <h2>TESTE DE RENDERIZA√á√ÉO</h2>
        <p style={{ fontSize: '24px', fontWeight: 'bold' }}>
          Categorias: <span style={{ color: 'green' }}>{categorias.length}</span>
        </p>
        <p style={{ fontSize: '24px', fontWeight: 'bold' }}>
          Produtos: <span style={{ color: 'green' }}>{produtos.length}</span>
        </p>
      </div>

      {/* TESTE DIRETO - SEM FILTROS */}
      <div style={{ backgroundColor: 'orange', padding: '20px', margin: '20px' }}>
        <h3>PRIMEIRO PRODUTO DO ARRAY:</h3>
        {produtos.length > 0 && (
          <div>
            <p><strong>Nome:</strong> {produtos[0].nome}</p>
            <p><strong>Pre√ßo:</strong> R$ {produtos[0].preco}</p>
          </div>
        )}
      </div>

      <footer style={{ marginTop: '50px' }}>
        <p>¬© 2024 Jetro's Lanches - TESTE</p>
      </footer>
    </div>
  );
}

export default App;
