import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [categorias, setCategorias] = useState([]);
  const [produtos, setProdutos] = useState([]);
  const [carregando, setCarregando] = useState(true);

  useEffect(() => {
    const carregarDados = async () => {
      try {
        console.log('üîÑ Iniciando busca dos dados...');
        
        // Usando o proxy, fazemos a requisi√ß√£o para a rota relativa
        const [resCategorias, resProdutos] = await Promise.all([
          fetch('/api/categorias'),
          fetch('/api/produtos')
        ]);

        console.log('‚úÖ Respostas recebidas:', resCategorias, resProdutos);

        const dadosCategorias = await resCategorias.json();
        const dadosProdutos = await resProdutos.json();

        setCategorias(dadosCategorias);
        setProdutos(dadosProdutos);
        setCarregando(false);
        
        console.log('üéâ Dados carregados!');
        
      } catch (erro) {
        console.error('‚ùå Erro detalhado:', erro);
        setCarregando(false);
      }
    };

    carregarDados();
  }, []);

  if (carregando) {
    return <div style={{ padding: '20px', textAlign: 'center' }}>Carregando card√°pio...</div>;
  }

  return (
    <div className="App">
      <h1>Card√°pio Jetro's Lanches</h1>
      <p>Total: {categorias.length} categorias e {produtos.length} produtos</p>
      {/* Renderize suas categorias e produtos aqui */}
    </div>
  );
}

export default App;
